import math
# Function to check if a number is prime
def is_prime(n):
 if n <= 1:
 return False
 for i in range(2, int(math.sqrt(n)) + 1):
 if n % i == 0:
 return False
 return True
# Function to check if a number can be expressed as the sum of two cubes in at least two unique ways
def is_sum_of_cubes(n):
 cubes = []
 for i in range(1, int(math.pow(n, 1/3)) + 1):
 cubes.append(i**3)
 for i in range(len(cubes)):
 for j in range(i, len(cubes)):
 if cubes[i] + cubes[j] == n:
 count = 0
 for k in range(len(cubes)):
