 if k != i and k != j and l != i and l != j and cubes[k] + cubes[l] == n:
 count += 1
 if count >= 1:
 return True
 return False
# Function to generate a list of numbers that are the sum of two cubes in at least two unique ways
def generate_sum_of_cubes_list(nums):
 result = []
 for num in nums:
 sum_of_cubes = num**3
 if is_sum_of_cubes(sum_of_cubes) and all(not is_prime(p) or p > 10 for p in set([2, 3, 5, 7]) & set(prime_factors(sum_of_cubes))):
 result.append(sum_of_cubes)
 return result
# Function to find prime factors of a number
def prime_factors(n):
 factors = []
 for i in range(2, int(math.sqrt(n)) + 1):
 while n % i == 0:
 factors.append(i)
 n //= i
 if n > 1:
 factors.append(n)
 return factors
